    justifyContent: "center",
    alignItems: "center",
    borderRadius: 4,
    marginLeft: 10
  },
  textButtonCreate: {
    color: colors.colorText,
    textAlign: "center",
    fontSize: 12,
    fontWeight: "700"
  },
  textListOfStaff: {
    color: colors.colorText,
    fontSize: 14,
    fontWeight: "700"
  },
  staff: {
    backgroundColor: colors.grayLight,
    borderRadius: 4,
    padding: 10,
    marginBottom: 12
  },
  formCreate: {
    paddingHorizontal: 10,
    backgroundColor: colors.grayLight,
    borderRadius: 4,
    marginBottom: 12
  },
  itemFormCreate: {
    alignItems: "center",
    justifyContent: "center",
    height: 41,
    marginBottom: 15,
    borderBottomWidth: 0.5,
    borderBottomColor: colors.colorLine,
    borderRadius: 10
  },
  textTitleItemInput: {
    color: colors.gray,
    fontWeight: "600",
    fontSize: 12,
    marginBottom: 9
  },
  itemInput: {
    height: 40,
    borderRadius: 4,
    backgroundColor: "#303030",
    paddingHorizontal: 11,
    color: colors.colorText
  },
  textButtonForm: {
    color: colors.colorText,
    fontSize: 16,
    fontWeight: "400"
  },
  buttonForm: {
    flex: 1,
    height: 36,
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 4
  }
